{{if .HasFilter}}WHERE {{if .HasTypeFilter}}"type" in({{range  $i, $e := .Types}}{{$e}}{{if notLast $i $.Types}},{{end}}{{end}}){{if .HasSetFilter}} and {{end}}{{end}}{{if .HasSetFilter}}({{range  $i, $e := .Sets}}"sets" like({{$e}}){{if notLast $i $.Sets}} or {{end}}{{end}}){{end}}{{end}}